import styled from 'styled-components';

export const MainApp = styled.div`
  padding: 3rem 0;
  width: 80%;
  max-width: 800px;
  text-align: center;
  margin: 0 auto;

  header {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    border-bottom: 1px solid lightgray;
  }
`;

export const RightHeading = styled.div`
  display: flex;
  flex-wrap: wrap;
  width: 32%;
  align-items: center;
  padding-left: 1rem;

  h1 {
    margin-left: 2rem;
    border-left: 2px solid black;
    padding-left: 1.5rem;
  }
`;

export const SearchBar = styled.div`
  display: flex;
  flex-wrap: wrap;
  width: 32%;
  align-items: center;
  justify-content: center;

  input {
    padding-left: 5px;
  }
`;
